import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth'

var config = {
    apiKey: "AIzaSyC-CL0yQ-D5ZLmuLWYNle4YTq0YJMQEiVM",
    authDomain: "alphaclothing-db.firebaseapp.com",
    databaseURL: "https://alphaclothing-db.firebaseio.com",
    projectId: "alphaclothing-db",
    storageBucket: "alphaclothing-db.appspot.com",
    messagingSenderId: "564979679816",
    appId: "1:564979679816:web:3ef631c25b06457d67ce7d",
    measurementId: "G-ZBQF8DR7EX"
};

export const createUserProfileDocument = async (userAuth, additionalData) => {
    if(!userAuth) return;

    const userRef = firestore.doc(`users/${userAuth.uid}`);
    const snapshot = await userRef.get();

    console.log(snapshot);

    if(!snapshot.exists){
        const {displayName, email} = userAuth;
        const createdAt = new Date();

        try{
            await userRef.set({
                displayName,
                email,
                createdAt,
                ...additionalData
            })
        }catch(error){
            console.log('error in creating user', error.message);
        }
    }

    return userRef;

}

firebase.initializeApp(config);

export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({prompt: 'select_account'});
export const signInWithGoogle = () => auth.signInWithPopup(provider);

export default firebase;
