import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey =
    "pk_test_51H0suzBK0YKCvpLK0uI2eB7Z6J46oT79IN5hlsRPTQJD6dxJk7Be2KZEJuP1MWiQp4XUX5cyYUFqT79EnydVOIFd00qIdeGo1q";

  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: priceForStripe,
        token,
      },
    })
      .then((response) => {
        alert("Payment Successful");
      })
      .catch((error) => {
        // console.log("Payment error: ", JSON.parse(error));
        console.log("Payment error: ", error);
        alert(
          // "There was an issue with your payment. Please make sure that you use the provided credit card."
          "There is a server issue with stipe payment in India, please try again."
        );
      });
  };

  return (
    <StripeCheckout
      label="Pay Now"
      name="ALPHA Clothing Pvt.Ltd."
      billingAddress
      shippingAddress
      image="https://svgshare.com/i/CUz.svg"
      description={`Your total price is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
